function()
    ElvUF_Player:ClearAllPoints()
    ElvUF_Player:SetPoint("RIGHT", aura_env.region, "LEFT", -2, -17)
    ElvUF_Target:ClearAllPoints()
    ElvUF_Target:SetPoint("LEFT", aura_env.region, "RIGHT", 2, -17)
    ElvUF_TargetTarget:ClearAllPoints()
    ElvUF_TargetTarget:SetPoint("LEFT", aura_env.region, "RIGHT", 271, -7)
    return
end

